    //   const cardLikeButtonClassName = (
    //     `element__button-like ${isLiked ? 'element__button-like_focus' : ''}`
    //   );

    function handleClick() {
        props.onCardClick(props.card);
      }  


    //   function handleLikeClick() {
    //     props.onCardLike(card);
    //   }

    //   function handleDeleteClick() {
    //     props.onCardDelete(card);
    //   }

    return (
        <>
            <li className="element">
                <img className="element__image" src={props.link} onClick={handleClick}/>
                <h2 className="element__name">{props.name}</h2>
                <button className="element__btn-like" type="button" />
                <button className="element__btn-remove" type="button" />
                <p className="element__likes" />
            </li>
        </>
    );
}

export default Card;
