// import logo from './logo.svg';
import './../index.css'
import Header from './Header.js'
import Main from "./Main.js"
import Footer from "./Footer.js"
import PopupWithForm from "./PopupWithForm.js"
import { useState } from "react";
import ImagePopup from './ImagePopup'
import { cards } from './Main.js' 
function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
  }
  function handleEditAvatarClick() {
    setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
  }
  function handleAddPlaceClick() {
    setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard({});
  }
  function handleCardClick(selectedCard) {
    setSelectedCard(selectedCard);
  }
  return (
    <div className='root'>
      <Header />
      <Main
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}


      />
      <Footer />
      <PopupWithForm name="profile" container="popup__container" title="Редактировать профиль" button="Сохранить" isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}>
        <label className="popup__label">
          <input
            id="input-name"
            type="text"
            name="name"
            className="popup__form-input popup__form-input_value_name"
            defaultValue="Жак-Ив Кусто"
            minLength={2}
            maxLength={40}
            required=""
            placeholder="Имя"
          />
          <span className="input-name-error popup__form-error" />
        </label>
        <label className="popup__label">
          <input
            id="input-job"
            type="text"
            name="about"
            className="popup__form-input popup__form-input_value_job"
            placeholder="О себе"
            defaultValue="Исследователь океана"
            minLength={2}
            maxLength={200}
            required=""
          />
          <span className="input-job-error popup__form-error" />
        </label></PopupWithForm>
      <PopupWithForm name="place" container="popup__container" title="Новое место" button="Сохранить" isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}>
        <label className="popup__label">
          <input
            id="input-place"
            type="text"
            name="name"
            className="popup__form-input popup__form-input_value_place"
            placeholder="Название"
            required=""
            minLength={2}
            maxLength={30}
          />
          <span className="input-place-error popup__form-error" />
        </label>
        <label className="popup__label">
          <input
            id="input-link"
            type="url"
            name="link"
            className="popup__form-input popup__form-input_value_link"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="input-link-error popup__form-error" />
        </label>
      </PopupWithForm>
      <PopupWithForm name="submit" container="popup__case" title="Вы уверены?" button="Да" />
      <PopupWithForm name="avatar" container="popup__avatar-box" title="Обновить аватар?" button="Сохранить" isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}>
        <label className="popup__label">
          <input
            id="input-avatar"
            type="url"
            name="avatar"
            className="popup__form-input popup__form-input_value_link"
          />
          <span className="input-avatar-error popup__form-error" />
        </label>
      </PopupWithForm>
      <ImagePopup card={selectedCard} onClose={closeAllPopups}/>
    </div>
  );
}


export default App;
