import { CurrentUserContext } from '../contexts/CurrentUserContext';
function Main(props) {
    const userInfo = React.useContext(CurrentUserContext);
    return (
        <main className="content">
            <section className="profile">
                <div className="profile__avatar-overlay" onClick={props.onEditAvatar}>
                    <img
                        className="profile__avatar"
                        alt="Фото Жака-Ив Кусто"
                        style={{ backgroundImage: `url(${userInfo.userAvatar})` }} 
                    />
                    <div className="profile__avatar-edit"></div>
                </div>
                <h1 className="profile__author">{userInfo.userName}</h1>
                <p className="profile__author-info">{userInfo.userDescription}</p>
                <button className="profile__btn-edit" type="button" onClick= {props.onEditProfile}></button>
                <button className="profile__btn-add" type="button" onClick ={props.onAddPlace}/>
            </section>
            <section className="elements">
                <ul className="elements__list">
                    <template id="template" />
                </ul>
            </section>
        </main>
    );
}

  

export default Main;
