import React from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function Card(props) {
    const currentUser = React.useContext(CurrentUserContext);
    const card = props.card; 
    const isOwn = card.owner._id === currentUser._id;

    function handleClick() {
        props.onCardClick(props.card);
    }

    return (
        <>
            <li className="element" onClick={handleClick}>
                <img className="element__image" src={props.link} />
                <h2 className="element__name">{props.name}</h2>
                <button className="element__btn-like" type="button" />
                {isOwn && <button className='button_del' />} 
                <p className="element__likes" />
            </li>
        </>
    );
}

export default Card;
