import React from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function Card(props) {
    const currentUser = React.useContext(CurrentUserContext);
    const isOwn = props.card.owner._id === currentUser._id;
    const isLiked = props.card.likes.some(i => i._id === currentUser._id);
    const cardLikeButtonClassName = ( 
        `element__btn-like ${isLiked && 'element__btn-like_active'}` 
      );; 

    function handleClick() {
        props.onCardClick(props.card);
    }

    function handleDeleteClick() {
        props.onCardDelete(props.card);
    }

    function handleLikeClick() {
        console.log('lalala');

        props.onCardLike(props.card);
    }
    return (
        <>
            <li className="element" onClick={handleClick}>
                <img className="element__image" src={props.link} />
                <h2 className="element__name">{props.name}</h2>
                <button onClick={handleLikeClick} type="button" className={cardLikeButtonClassName}></button>
                {isOwn && <button className="element__btn-remove" onClick={handleDeleteClick} />}
                <p className="element__likes" />
            </li>
        </>
    );
}

export default Card;
