function Main() {
    return (
        <main className="content">
            <section className="profile">
                <div className="profile__avatar-overlay" onClick={handleEditAvatarClick}>
                    <img
                        className="profile__avatar"
                        src="<%=require('./images/Kusto.jpg')%>"
                        alt="Фото Жака-Ив Кусто"
                    />
                    <div className="profile__avatar-edit"></div>
                </div>
                <h1 className="profile__author">Жак-Ив Кусто</h1>
                <p className="profile__author-info">Исследователь океана</p>
                <button className="profile__btn-edit" type="button" onClick={handleEditProfileClick}/>
                <button className="profile__btn-add" type="button" onClick ={handleAddPlaceClick}/>
            </section>
            <section className="elements">
                <ul className="elements__list">
                    <template id="template" />
                </ul>
            </section>
        </main>
    );
}

function handleEditAvatarClick() {
    const popupAvatar = document.querySelector('.popup_avatar');
    popupAvatar.classList.add("popup_opened");
}
function handleEditProfileClick() {
    const popupAvatar = document.querySelector('.popup_profile');
    popupAvatar.classList.add("popup_opened");
}
function handleAddPlaceClick() {
    const popupAvatar = document.querySelector('.popup_place');
    popupAvatar.classList.add("popup_opened");
}

export default Main;
