import { useContext } from 'react'
import Card from '../components/Card.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';

function Main(props) {

    const cards = props.cards;
    const currentUser = useContext(CurrentUserContext);

    return (
        <>
            <main className="content">
                <section className="profile">
                    <div className="profile__avatar-overlay" onClick={props.onEditAvatar}>
                        <img
                            className="profile__avatar"
                            alt="Фото Жака-Ив Кусто"
                            src={currentUser.avatar}
                        />
                        <div className="profile__avatar-edit"></div>
                    </div>
                    <h1 className="profile__author">{currentUser.name}</h1>
                    <p className="profile__author-info">{currentUser.about}</p>
                    <button className="profile__btn-edit" type="button" onClick={props.onEditProfile}></button>
                    <button className="profile__btn-add" type="button" onClick={props.onAddPlace} />
                </section>
                <section className="elements">
                    <ul className="elements__list">
                        <template id="template" />
                        {cards.map((item) => (
                            <Card
                                key={item._id}
                                onCardClick={props.onCardClick}
                                card={item}
                                link={item.link}
                                name={item.name}
                                onCardDelete={props.onCardDelete}
                                onCardLike={props.onCardLike}
                                like={item.likes}
                            />
                        ))};
                    </ul>
                </section>
            </main>
        </>
    );
}



export default Main;
