import './../index.css'
import Header from './Header.js'
import Main from "./Main.js"
import Footer from "./Footer.js"
import PopupWithForm from "./PopupWithForm.js"
import { useState, useEffect } from "react";
import ImagePopup from './ImagePopup'
import { CurrentUserContext } from './../contexts/CurrentUserContext.js';
import newApi from '../utils/Api.js';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';
import { Routes, Route, Navigate } from 'react-router-dom';
import ProtectedRoute from "./ProtectedRoute.js";
import InfoTooltip from './InfoTooltip.js';
import Register from './Register.js';
import Login from './Login.js';
import * as auth from '../utils/Auth.js';



function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [currentUser, setUserInfo] = useState({});
  const [cards, setCards] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);
  const [isInfoTooltip, setInfoTooltip] = useState({ isOpen: false, successful: false });

  useEffect(() => {
    newApi
      .getUserData()
      .then((userInfo) => {
        setUserInfo(userInfo);
      })
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    newApi
      .getAllCards()
      .then((userCards) => {
        setCards(userCards);
      })
      .catch((err) => console.log(err))
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      auth.checkToken(token)
        .then(data => {
          if (data) {
            setEmail(data.data.email);
            handleLoggedIn();
            history.push('/');
          }
        })
    }
  }, [history]);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some((like) => like._id === currentUser._id);
    newApi.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      })
      .catch((err) => console.log(err));
  }

  function handleCardDelete(card) {
    newApi
      .removeCard(card._id)
      .then((res) => {
        const newCard = cards.filter((item) => item._id !== card._id);
        setCards(newCard);
      })
      .catch((err) => console.log(err))
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard({});
    setInfoTooltip(false);
  }

  function handleCardClick(selectedCard) {
    setSelectedCard(selectedCard);
  }

  function handleUpdateUser({ name, about }) {

    newApi
      .editProfileInfo({
        name: name,
        about: about,
      })
      .then((res) => {
        setUserInfo(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err))
  }

  function handleUpdateAvatar(link) {

    newApi
      .editAvatar(link)
      .then((res) => {
        setUserInfo(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err))
  }

  function handleAddPlaceSubmit({ name, link }) {

    newApi
      .addNewCard({
        name: name,
        link: link
      })
      .then((data) => {
        const newCard = data;
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.log(err))
  }

  function handleLoggedIn() {
    setLoggedIn(true);
  }

  function handleRegister(password, email) {
    auth.register(password, email)
      .then(data => {
        if (data) {
          handleInfoTooltip(true);
          history.push('/sign-in');
        }
      })
      .catch(err => {
        console.log(err);
        handleInfoTooltip(false);
      })
  }

  function handleLogin(password, email) {
    auth.login(password, email)
      .then(data => {
        if (data.token) {
          setEmail(email);
          handleLoggedIn();
          localStorage.setItem('token', data.token);
          history.push('/');
        }
      })
      .catch(err => {
        handleInfoTooltip(false);
        console.log(err);
      })
  }

  function handleSignOut() {
    localStorage.removeItem('token');
    setLoggedIn(false);
    setEmail('');
    history.push('/sign-in');
  }



  return (
    <>
      <CurrentUserContext.Provider value={currentUser}>
        <Header email={email} onSignOut={handleSignOut} />       
         <Routers>
          <ProtectedRoute
            exact path='/'
            loggedIn={loggedIn}
            component={Main}
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onEditAvatar={handleEditAvatarClick}
            onCardClick={handleCardClick}
            cards={cards}
            onCardLike={handleCardLike}
            handleCardDelete={handleCardDelete}

          />

          <Route path="/sign-in">
            <Login onLogin={handleLogin} />
          </Route>

          <Route path="/sign-up">
            <Register onRegister={handleRegister} />
          </Route>

          <Route>
            {loggedIn ? (
              <Navigate to="/" />
            ) : (
              <Navigate to="/sign-in" />
            )}
          </Route>
        </Routers>

        <Footer />

        <InfoTooltip result={isInfoTooltip} onClose={closeAllPopups} />

        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />

        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />

        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlace={handleAddPlaceSubmit}
        />


        <PopupWithForm name="submit" container="popup__case" title="Вы уверены?" button="Да" />
        <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      </CurrentUserContext.Provider>
    </>
  );
}


export default App;
